//prompt: something wonderful and detailed 
vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0);

    // Generate multiple interference patterns using UV coordinates
    float d1 = length(uv * 5.0 + iTime * 0.7);
    float d2 = sin((uv.x * 8.0 + iTime) * 6.0) * cos((uv.y * 8.0 + iTime) * 6.0);

    // Create a checkerboard-like pattern with UV
    vec2 coord = fract(uv * 4.0 + iTime * 0.5);
    float check1 = step(0.5, coord.x) - step(0.5, coord.y);
    float check2 = fract(coord.x * 3.0) - fract(coord.y * 3.0);

    // Combine patterns with noise
    float noiseVal = noise(vec2(d1, d2)) * 0.8;
    float pattern = sin(d1 + iTime * 0.5) * cos(d2 + iTime * 0.3) + noiseVal;

    // Calculate color channels based on combined patterns
    color.r = abs(sin(pattern * 4.0 + d1)) * 0.8;
    color.g = abs(cos(pattern * 4.0 + d2)) * 0.6;
    color.b = pattern / 3.0 + noiseVal * 0.4;

    return vec4(color, 1.0);
}
