//prompt: a fractal
vec4 Effect(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 3.0 - 1.5;
    float r = length(st);
    float a = atan(st.y, st.x);

    // Create expanding rings pattern
    float rings = floor(r * 4.0 + iTime) / 4.0;

    // Add rotation to the pattern
    a += rings * 2.0 + iTime;

    // Create secondary pattern using sine waves
    float pattern = sin(a * 3.0 + rings * 8.0) * cos(rings * 5.0 + r);

    // Combine patterns and create color variations
    vec3 color = mix(
        vec3(rings, fract(rings * 2.0), fract(rings * 3.0)),
        vec3(fract(pattern + rings), fract(pattern - rings), fract(pattern * rings)),
        abs(sin(a * 4.0))
    );

    return vec4(color, 1.0);
}
