//prompt: a sdf of a weird shape
vec4 Effect2(vec2 uv) {
    // Create a weird shape using signed distance function
    vec2 distUV = uv * 10.0 + iTime;

    float gridX = sin(distUV.y * 5.0) / (sin(distUV.x * 3.0 + iTime) + 1.0);
    float gridY = cos(distUV.x * 7.0) / (cos(distUV.y * 2.0 - iTime) + 1.0);

    float shape = sin(gridX * 3.0 + iTime * 2.0) * 
                 cos(gridY * 5.0 - iTime) +
                 length(uv * 4.0) *
                 (sin(length(uv) * 8.0) / 2.0);

    float noise = fract(sin(dot(uv, vec2(123.45, 678.9))) * 4321.0);

    shape = abs(shape + noise) * 0.5;

    return vec4(vec3(shape), 1.0);
}
