//prompt: a wavy grid

vec4 Effect2(vec2 uv) {
    // Apply some distortion to the coordinates
    vec2 distortedUV = uv + 0.5 * sin(uv * 10.0 + iTime);

    // Create grid lines with warping effect
    float horizontal = abs(sin(distortedUV.y * 10.0));
    float vertical = abs(sin(distortedUV.x * 10.0));

    // Combine and color the grid
    vec3 color = mix(
        vec3(0.2, 0.5, 1.0), // Blue-ish for horizontal lines
        vec3(1.0, 0.3, 0.3), // Red-ish for vertical lines
        step(0.9, horizontal + vertical)
    );

    return vec4(color, 1.0);
}
