vec4 Effect2(vec2 uv) {
    vec3 color = hsl(
        sin(uv.x * iTime) * 0.5 + 0.5,
        cos(uv.y * iTime) * 0.5 + 0.5,
        noise(uv * iTime) * 0.5 + 0.5
    );

    float timeFactor = mod(iTime * 2.0, 1.0);
    color = hsl2rgb(color * vec3(1.0 - timeFactor, 1.0 - timeFactor, 1.0));

    float noiseValue = noise(vec2(uv.x * iTime, uv.y * iTime));
    color *= hsv2rgb(vec3(
        mod(iTime * 5.0 + uv.x, 1.0),
        0.8,
        0.8
    ));

    vec4 result;
    result.rgb = color;
    result.a = 1.0;
    return result;
}
