vec4 Effect2(vec2 uv) {
    // Create a moving point based on UV coordinates and time
    vec3 p = vec3(uv * 10.0 + iTime, iTime);

    // Add some turbulence for organic movement
    float t = turbulent(p * 0.5);

    // Generate color using HSL with time-based parameters
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.1) * 0.5 + 0.5,   // Hue variation
        0.7 + t * 0.3,                  // Saturation
        0.5 + sin(length(uv) * 8.0 + iTime * 2.0) * 0.5  // Lightness modulation
    ));

    // Add some intensity based on position and time
    float intensity = sin(length(uv) * 4.0 + iTime) * 0.5 + 0.5;
    color *= intensity;

    return vec4(color, 1.0);
}
