vec4 Effect2(vec2 uv) {
    // Create a base pattern using noise and turbulence
    float time = iTime * 0.5;

    // Scale UV coordinates for more detailed patterns
    vec2 st = fract(uv * 100.0);

    // Add some movement to the pattern
    st += vec2(sin(time + st.x) * 0.3, cos(time + st.y) * 0.3);

    // Use turbulence to create a complex noise pattern
    float turb = turbulent(vec3(st, time));

    // Create color variations using HSL conversion
    vec3 color1 = hsl2rgb(vec3(turb * pi, 0.7, 0.5));
    vec3 color2 = hsl2rgb(vec3(turb * pi + time, 0.6, 0.4));

    // Mix colors based on noise values
    vec3 finalColor = mix(color1, color2, noise(st) * 0.5 + 0.5);

    // Add some dynamic lighting effect
    float light = sin(time + length(uv - 0.5)) * 0.5 + 0.5;
    finalColor *= light;

    return vec4(finalColor, 1.0);
}
