vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Animate UV coordinates with time
    float t = iTime * 0.5;
    st.x += sin(t) * 0.3;
    st.y += cos(t) * 0.3;

    // Add noise to the UV coordinates for perturbation
    vec2 noiseUV = st * 10.0 + vec2(sin(t), cos(t));
    float n = noise(noiseUV);

    // Transform and scale the noise pattern
    float scale = mod(iTime * 4.0, 10.0) / 10.0;
    n = fract(n * scale + t);

    // Rotate based on time for additional movement
    float angle = atan2(st.y, st.x) + t;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 transformedUV = vec2(st.x, st.y) * 0.5 + 0.5;

    // Calculate color using HSL
    vec3 color = hsl(n * 0.5, 1.0, 0.5);

    return vec4(color, 1.0);
}
