vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a base color using HSL to RGB conversion
    vec3 baseColor = hsl2rgb(vec3(0.6 + sin(time * 0.7) * 0.3, 0.8, 0.1));

    // Calculate distance from center
    vec2 st = uv * 2.0 - 1.0;
    float d = length(st);

    // Add some noise and turbulence
    float t = turbulent(vec3(uv * 5.0 + time, time));
    float glow = sin(d * 8.0 + time) * 0.5 + 0.5;

    // Create a pulsing effect
    vec3 color = mix(baseColor, hsl2rgb(vec3(0.1 + sin(time * 0.3), 0.9, 0.7)), glow);

    // Add some movement and complexity
    float movement = sin(time) * 0.5;
    color += noise(vec3(uv * 4.0 + time, movement)) * 0.2;

    // Create a central glowing area
    vec3 finalColor = mix(color, hsl2rgb(vec3(0.9, 1.0, 0.8)), smoothstep(0.7, 0.5, d));

    return vec4(finalColor, 1.0);
}
