vec4 Effect2(vec2 uv) {
    // Apply turbulence distortion to UV coordinates
    vec2 distortedUV = uv + turbulent(uv * 10.0 + iTime) * 0.3;

    // Generate noise patterns at different scales
    float noise1 = noise(distortedUV * 5.0);
    float noise2 = noise(distortedUV * 10.0 + vec2(1.0, 2.0));

    // Create color shifts using HSL conversion
    vec3 baseColor = hsl(
        sin(iTime) * 0.5 + 0.5,
        0.7,
        0.6
    );

    // Add glitch effects with noise and time
    vec3 finalColor = mix(
        baseColor,
        hsl(
            (sin(iTime * 2.0) + noise1) * 0.5 + 0.5,
            0.8,
            (noise2 + sin(uv.x * pi)) * 0.5 + 0.5
        ),
        abs(sin(iTime)) * 0.3 + 0.2
    );

    // Add some turbulence-based color variation
    finalColor += turbulent(vec3(distortedUV, iTime)) * 0.1;

    return vec4(finalColor, 1.0);
}
