vec4 Effect2(vec2 uv) {
    vec2 uvTime = uv + vec2(sin(iTime), cos(iTime)) * 0.3;

    float scale = 5.0;
    vec2 noiseUV = (uvTime * scale) * 0.1;

    float baseNoise = noise(noiseUV);
    float turbulence1 = turbulent(vec2(noiseUV.x * 3.0, noiseUV.y * 3.0));
    float turbulence2 = turbulent(vec2(noiseUV.x * 6.0, noiseUV.y * 6.0)) * 0.5;

    float displacement = baseNoise + (turbulence1 + turbulence2) * 0.7;

    vec3 colorParams = vec3(
        sin(displacement * PI + iTime) * 0.5 + 0.5,
        cos(iTime * 2.0) * 0.4 + 0.6,
        sin((displacement * PI) * 0.5 + iTime * 1.5) * 0.3 + 0.7
    );

    vec3 color = hsl2rgb(colorParams);

    return vec4(color, 1.0);
}
