vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Add some wobble and twist
    theta += sin(theta * 3.0 + iTime) * 0.2;
    theta += cos(r * 10.0 + iTime * 2.0) * 0.1;

    // Create twirly pattern with noise
    vec2 noisePos = vec2(
        sin(theta) * r * 0.5 + iTime,
        cos(theta) * r * 0.5 + iTime * 0.3
    );
    float n = noise(noisePos * 10.0);

    // Add sparkles with turbulence
    float t = turbulent(vec2(
        sin(theta * 2.0) * r * 0.5 + iTime,
        cos(theta * 2.0) * r * 0.5 + iTime * 0.3
    ) * 10.0);

    // Create color from HSL values
    vec3 hslColor = vec3(
        theta * 0.15 + iTime * 0.1,
        abs(sin(iTime * 0.2)) * 0.7 + 0.3,
        n * 0.5 + t * 0.5 + 0.5
    );

    // Convert to RGB and add sparkles
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= n * 2.0;
    rgbColor += t * 0.3;

    // Add some wobble to the final color
    rgbColor.r += sin(iTime) * 0.1;
    rgbColor.g += cos(iTime * 0.7) * 0.1;
    rgbColor.b += sin(iTime * 0.5) * 0.1;

    return vec4(rgbColor, 1.0);
}
