vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create three prongs for biohazard symbol
    float angleStep = pi / 3.0;
    float prongWidth = 0.15;

    // Calculate each prong position with turbulence
    float prong1 = step(r, 0.8 - turbulent(vec2(theta + angleStep * 0.0, iTime * 0.1)));
    float prong2 = step(r, 0.8 - turbulent(vec2(theta + angleStep * 1.0, iTime * 0.1)));
    float prong3 = step(r, 0.8 - turbulent(vec2(theta + angleStep * 2.0, iTime * 0.1)));
    // Combine prongs and add noise to center
    float combined = (prong1 + prong2 + prong3) / 3.0;
    combined += step(r, 0.4) * turbulent(vec2(iTime * 0.5));
    // Create color transitions using HSL
    vec3 color1 = hsl(0.6, 0.8, 0.7);
    vec3 color2 = hsl(0.3, 0.8, 0.7);
    vec3 color3 = hsl(0.0, 0.8, 0.7);
    // Mix colors based on position
    float mix1 = smoothstep(0.6, 0.9, r) * sin(theta + iTime);
    float mix2 = smoothstep(0.6, 0.9, r) * cos(theta + iTime);

    vec3 finalColor = mix(color1, color2, mix1) + mix(color3, color1, mix2);
    // Apply combined shape to colors
    return vec4(finalColor * combined, 1.0);
}
