vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Create rotating pattern using time
    float t = iTime * 0.5;
    mat2 rot = mat2(cos(t), -sin(t), sin(t), cos(t));
    vec2 coord = (st - 0.5) * rot;

    // Add noise and turbulence effects
    float noise1 = turbulent(coord * 4.0 + t);
    float noise2 = noise(vec3(coord * 8.0, t));

    // Create color variations using HSL
    vec3 color1 = hsl(0.5 + noise1 * 0.2, 0.7, 0.3);
    vec3 color2 = hsl(0.4 + noise2 * 0.3, 0.6, 0.4);

    // Combine colors with radial gradient
    float dist = length(coord) * 1.5;
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, dist));

    // Add depth effect using distance from center
    float depth = 1.0 - length(st - 0.5) * 2.0;
    finalColor *= max(0.0, depth);

    return vec4(finalColor, 1.0);
}
