vec4 Effect2(vec2 uv) {
    // Center the UV coordinates
    vec2 st = uv - 0.5;

    // Create a circular pattern
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Add some rotation and turbulence
    float rot = r * 10.0 + iTime * 2.0;
    vec2 flow = vec2(sin(theta + rot), cos(theta + rot)) * (1.0 - r) * 0.5;

    // Use noise to create a more organic pattern
    vec2 noisePos = st * 4.0 + flow * 2.0;
    float n = noise(noisePos);

    // Create color based on HSL values
    vec3 color = hsl2rgb(vec3(
        (r * 180.0 + iTime) / 360.0,   // Hue
        0.7 + n * 0.3,                 // Saturation
        0.5 + sin(iTime) * 0.2         // Lightness
    ));

    // Add some highlights
    color += cos(theta - iTime) * 0.1;

    return vec4(color, 1.0);
}
