vec4 Effect2(vec2 uv) {
    // Create polar coordinates from UV
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create a mask using noise and turbulence
    float mask = turbulent(vec3(uv * 5.0 + iTime, 0.0)) * 0.5 + 0.5;
    mask *= turbulent(vec2(uv * 10.0 + iTime)) * 0.5 + 0.5;
    // Create a swirling pattern
    float swirl = sin(theta * 4.0 + iTime) * cos(r * 8.0 - iTime);

    // Combine mask and swirl to create the river hole effect
    vec3 color = hsl2rgb(vec3(
        (theta / (2.0 * PI) + 0.5) * 120.0,   // Hue varies with angle
        0.7 + 0.3 * mask,                     // Saturation
        0.8 - 0.5 * swirl                      // Lightness
    ));
    return vec4(color, 1.0);
}
