vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern
    vec2 st = uv * 2.0 - 1.0;

    // Add noise layers with different scales
    float n1 = noise(st * 200.0 + iTime);
    float n2 = turbulent(st * 50.0 + iTime * 0.5);
    float n3 = noise(vec3(st * 100.0, iTime));

    // Create a rotating pattern
    float angle = atan2(st.y, st.x) + iTime;
    vec2 rotated = vec2(
        cos(angle) * st.x - sin(angle) * st.y,
        sin(angle) * st.x + cos(angle) * st.y
    );

    // Combine noise layers with color transformations
    vec3 color1 = hsl2rgb(vec3(0.5 + n1 * 0.5, 0.7, 0.8));
    vec3 color2 = hsv2rgb(vec3(0.3 + n2 * 0.3, 0.6, 0.7));

    // Mix colors based on noise values
    vec3 finalColor = mix(color1, color2, rotated.x * 0.5 + 0.5);

    // Add pulsing effect using time
    float pulse = sin(iTime) * 0.5 + 0.5;
    finalColor *= 1.0 + n3 * pulse * 0.5;

    return vec4(finalColor, 1.0);
}
