vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create a pulsing effect with time
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Add some noise and turbulence
    float noiseVal = turbulent(vec3(uv * 10.0, iTime)) * 0.2;
    float petal = sin(theta * 6.0 - iTime * 2.0) * (r + noiseVal);

    // Create color transitions
    vec3 color = hsl(
        mod(iTime * 0.1 + theta * 4.0, 1.0),
        0.7,
        0.5 + sin(r * 8.0 - iTime) * 0.2
    );

    // Combine effects for final output
    float alpha = smoothstep(0.9, 1.0, r + pulse);
    return vec4(color, alpha);
}
