vec4 Effect2(vec2 uv) {
    float scale = 50.0;
    vec3 col = vec3(0.0);

    // Base noise pattern
    float n = noise(uv * scale + iTime);

    // Add turbulence at different scales
    float t1 = turbulent(uv * scale + iTime);
    float t2 = turbulent(uv * (scale * 0.5) + iTime * 0.5);
    float t3 = turbulent(uv * (scale * 0.25) + iTime * 0.25);

    // Combine noise and turbulence
    col += hsl2rgb(vec3(
        n * 0.5 + 0.5,
        (t1 + t2 + t3) * 0.333,
        0.7
    ));

    // Add geometric pattern
    float angle = atan2(uv.y, uv.x);
    float dist = length(uv);
    float geo = sin(dist * 5.0 + iTime) * sin(angle * 4.0);
    col += vec3(geo * 0.5 + 0.5);

    // Final color with mix of noise and geometry
    col = (col + vec3(n, t1, t2)) * 0.5;

    return vec4(col, 1.0);
}
