vec4 Effect2(vec2 uv) {
    // Remap UV from [0,1] to [-1,1]
    vec2 st = uv * 2.0 - 1.0;

    // Add some movement over time
    float time = iTime * 0.5;

    // Create a rotating pattern
    float angle = atan2(st.y, st.x) + time;
    vec2 rotated = vec2(
        cos(angle) * st.x - sin(angle) * st.y,
        sin(angle) * st.x + cos(angle) * st.y
    );

    // Add some noise and turbulence
    float noise1 = turbulent(rotated * 4.0);
    float noise2 = turbulent(vec3(rotated * 8.0, time));

    // Create color from HSL values
    vec3 color = hsl(
        fract(time + atan2(st.y, st.x) / (PI * 2.0)) * 360.0,
        0.7 + noise1 * 0.3,
        0.5 + noise2 * 0.5
    );

    // Add some geometric patterns
    float rad = length(st);
    float circles = sin(rad * 8.0 + time) * 0.5 + 0.5;
    color *= circles;

    return vec4(color, 1.0);
}
