vec4 Effect2(vec2 uv) {
    // Create a rotating polar coordinate system
    vec2 st = uv * 5.0 - 2.5;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.1;

    // Add some turbulence to create organic patterns
    float t = turbulent(vec3(r, a, iTime * 0.2));

    // Create flower-like pattern using sine waves
    float petal = sin(r * 8.0 - a * 4.0 + iTime) * 0.5;
    float ring = sin(r * 16.0 + iTime) * 0.3;

    // Combine patterns and add depth with noise
    float pattern = (petal + ring + t) * 0.5;
    pattern += noise(vec2(a, r)) * 0.2;

    // Create color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        a * 0.1 + iTime * 0.1,
        0.7 + t * 0.3,
        0.5 + pattern * 0.4
    ));

    // Add depth and movement to the final output
    color *= (1.0 - noise(vec2(r, a)) * 0.5);
    color *= (1.0 - r * 0.2);

    return vec4(color, 1.0);
}
