vec4 Effect2(vec2 uv) {
    // Rotate coordinates around center
    vec2 st = uv * 2.0 - 1.0;
    float t = iTime * 0.5;

    // Apply rotation matrix
    vec2 rotated = vec2(
        st.x * cos(t) - st.y * sin(t),
        st.x * sin(t) + st.y * cos(t)
    );

    // Add noise and turbulence
    float noiseVal = noise(rotated * 0.5 + iTime);
    float turb = turbulent(rotated * 10.0);

    // Create orbital pattern using polar coordinates
    vec2 polar = rotated;
    float angle = atan2(polar.y, polar.x) + t;
    float radius = length(polar) * 0.75;

    // Generate color using HSL to RGB conversion
    vec3 color = hsl(angle * 0.1, 0.8, 0.6);
    color *= (1.0 + turb * 0.5);

    // Add radial gradient
    float dist = length(rotated) * 0.5;
    color *= 1.0 - dist;

    return vec4(color, 1.0);
}
