vec4 Effect2(vec2 uv) {
    // Create flowing liquid metal effect using noise and turbulence

    // Add time-based distortion to UV coordinates
    vec2 distortedUV = uv * 5.0 + vec2(iTime, iTime * 0.7);
    distortedUV += turbulent(distortedUV * 0.5) * 0.3;

    // Generate base color with metallic feel
    vec3 color = vec3(0.1, 0.1, 0.1); // Dark gray

    // Add shimmering effect using noise and time
    float noiseValue = noise(distortedUV);
    color += mix(vec3(0.5, 0.7, 1.0), // Cyan-ish
                 vec3(1.0, 0.4, 0.8), // Magenta-ish
                 noiseValue) * (noiseValue + sin(iTime) * 0.5);

    // Add flowing highlights
    vec2 highlightUV = uv * 2.0 + vec2(sin(iTime) * 3.0, cos(iTime) * 2.0);
    float highlight = turbulent(highlightUV * 0.5) * 0.7;
    color += vec3(1.0, 0.8, 0.6) * (highlight + sin(length(uv)) * 0.3);

    // Mix in some yellow for warmth
    color += vec3(1.0, 1.0, 0.4) * noise(distortedUV + iTime) * 0.2;

    // Ensure colors don't overflow and apply gamma correction
    return vec4(pow(color / 1.5, vec3(1.0/2.2)), 1.0);
}
