vec4 Effect2(vec2 uv) {
    // Create a dark background color
    vec3 bgColor = hsl2rgb(vec3(0.5, 1.0, 0.1));

    // Calculate distance from center
    vec2 pos = uv - 0.5;
    float dist = length(pos);
    float distSq = dist * dist;

    // Create circular pattern with noise
    float timeFactor = sin(iTime * 0.5) * 0.5 + 0.5;
    float noise1 = turbulent(vec2(uv.x * 4.0, uv.y * 4.0)) * 0.5 + 0.5;
    float noise2 = turbulent(vec3(uv * 8.0, iTime * 0.2)) * 0.5 + 0.5;

    // Combine elements
    vec3 color = hsl2rgb(vec3(
        0.6 + noise1 * 0.3,
        0.7 + noise2 * 0.3,
        0.2 + timeFactor * 0.3
    ));

    // Add circular gradient effect
    float gradient = smoothstep(0.8, 1.0, dist);
    color *= mix(vec3(1.0), vec3(0.5), gradient);

    return vec4(color, 1.0);
}
