vec4 Effect2(vec2 uv) {
    // Create base frequencies for noise
    float freq1 = sin(iTime * 0.5 + 1.0);
    float freq2 = sin(iTime * 0.7 + 2.0);

    // Create a central point of interest
    vec2 center = uv - vec2(0.5);
    float dist = length(center) * 4.0;
    dist += iTime * 2.0;

    // Generate noise values with different scales
    float n1 = noise(vec3(uv * freq1 * 2.0 + iTime, iTime));
    float n2 = noise(vec3(uv * freq2 * 3.0 + iTime*2.0, iTime*0.5));

    // Create a color transition effect
    vec3 color = hsl(
        sin(iTime * 0.1) * 0.5 + 0.5,
        0.7 + n1 * 0.3,
        0.5 + n2 * 0.5
    );

    // Add turbulence effect
    float turb = turbulent(vec2(uv.x * 4.0, uv.y * 4.0 + iTime));
    color *= pow(abs(turb), 2.0);

    // Combine everything for the final effect
    vec3 finalColor = mix(
        color,
        hsl(1.0 - sin(iTime * 0.1) * 0.5, 0.8, 0.6),
        pow(sin(dist * 0.2 + iTime), 4.0)
    );

    return vec4(finalColor, 1.0);
}
