vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating hexagonal pattern
    theta += iTime * 6.0; // Rotate over time

    // Create gradient fill based on angle and radius
    vec3 color = hsl(theta * 0.15 + 0.5, 0.7, 0.6);

    // Add pulsating center effect
    float pulse = sin(iTime) * 0.2;
    r += pulse;

    // Create hexagonal shape with multiple layers
    float d = mod(theta * 6.0 + iTime, pi * 2.0);
    float layer1 = smoothstep(0.7, 0.8, abs(r - 0.5));
    float layer2 = smoothstep(0.6, 0.7, abs(r - 0.3));
    float layer3 = smoothstep(0.5, 0.6, abs(r - 0.1));

    // Combine layers and add noise
    float final = (layer1 + layer2 + layer3) * 0.4;
    final += turbulent(st * 8.0 + iTime) * 0.2;

    // Convert to RGB and return
    vec3 finalColor = hsl2rgb(color * final);
    return vec4(finalColor, 1.0);
}
