vec4 Effect2(vec2 uv) {
    // Create a base color using HSL with time-based animation
    vec3 baseColor = hsl(
        sin(iTime * 0.5 + uv.x * 10.0) * 0.5 + 0.5,  // Hue variation
        0.7,  // Saturation
        0.5   // Lightness
    );

    // Add noise and turbulence effects
    vec2 noiseUV = uv * 8.0 + iTime;
    float noiseValue = noise(noiseUV);
    float turbulentValue = turbulent(noiseUV * 4.0);

    // Combine effects with color transformations
    vec3 finalColor = baseColor * (noiseValue * 0.5 + 0.5) + 
                     hsv2rgb(vec3(0.1, 0.8, 1.0)) * turbulentValue;

    return vec4(finalColor, 1.0);
}
