vec4 Effect2(vec2 uv) {
    // Create rotating pattern using polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x) + iTime; 
    float r = length(st);

    // Add noise and turbulence in radial direction
    float tr = turbulent(vec3(r*5.0, iTime*0.5, 0.0));
    float ttheta = turbulent(vec3(theta*2.0, iTime*0.7, 0.0));

    // Create color from combined noise patterns
    vec3 color = hsl(
        (tr + ttheta) * 180.0,
        0.6 + 0.4*sin(iTime*0.5),
        0.5 + 0.5*cos(tr*2.0 + iTime)
    );

    // Add some layering and depth
    float layer1 = turbulent(vec3(r*10.0, theta*3.0, iTime));
    float layer2 = turbulent(vec3(r*5.0, theta*6.0, iTime+1.0));

    color *= hsl2rgb(vec3(
        (layer1 + layer2)*180.0,
        0.4,
        0.7
    ));

    // Add some tonal adjustments and vignette
    color = mix(color, vec3(0.0), smoothstep(0.95, 1.0, r));
    color *= 0.8 + 0.2*sin(iTime);

    return vec4(hsl2rgb(color), 1.0);
}
