vec4 Effect2(vec2 uv) {
    // Create a time-varying frequency
    float t = iTime * 0.5;

    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st) * 4.0;

    // Add noise and turbulence
    theta += noise(vec3(theta / 8.0 + t, r / 8.0 + t, 0.0)) * 0.5;
    r += turbulent(vec2(r / 8.0 + t, theta / 8.0 + t)) * 0.5;

    // Create color from HSL
    vec3 color = hsl(
        mod(theta * 0.1 + t, 1.0),  // Hue
        0.7 + noise(vec2(r / 4.0 + t, theta / 4.0 + t)) * 0.3,  // Saturation
        0.5 + sin(r * 0.1 + t) * 0.5  // Lightness
    );

    return vec4(hsl2rgb(color), 1.0);
}
