vec4 Effect2(vec2 uv) {
    // Rotate coordinates over time
    float t = iTime * 2.0;
    vec2 rotatedUv = uv * mat2(cos(t), sin(t), -sin(t), cos(t));

    // Generate noise patterns with different scales
    float n1 = noise(rotatedUv * 50.0);
    float n2 = turbulent(rotatedUv * 100.0 + vec2(3.14, 2.718));

    // Create color variations using HSL to RGB conversion
    vec3 baseColor = hsl(
        sin(iTime * 0.5) * 0.5 + 0.5,
        0.6,
        0.5
    );

    // Mix noise values with UV coordinates and time
    float mixVal = (n1 + n2 * 0.5 + sin(rotatedUv.x * 10.0) * 0.3) * 0.5 + 0.5;

    // Create final color by mixing base color with noise-influenced values
    vec3 finalColor = mix(
        baseColor,
        hsl2rgb(vec3(
            (n1 + rotatedUv.x) * 0.5 + 0.5,
            0.7,
            0.6
        )),
        mixVal
    );

    // Add some time-based pulsing to the color
    finalColor *= sin(iTime * 0.3) * 0.5 + 0.5;

    return vec4(finalColor, max(n1, n2) * 0.5 + 0.5);
}
