vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x);
    // Add time-based movement and rotation
    radius += sin(iTime * 0.5 + angle) * 0.3;
    angle += iTime * 0.2;
    // Create geometric pattern using polar coordinates
    vec2 polar = vec2(radius, angle);
    float pattern = turbulent(polar * 10.0) * 0.5 + 0.5;

    // Generate dynamic color transitions
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.1 + pattern * 0.5) * 360.0,
        0.7,
        0.5
    ));
    // Add noise and turbulence for more depth
    float noiseValue = noise(vec3(uv * 20.0, iTime));
    color *= mix(1.0, noiseValue, 0.3);

    return vec4(color, 1.0);
}
