vec4 Effect2(vec2 uv) {
    // Dynamic frequency based on time
    float freq = 5.0 + sin(iTime * 0.5) * 3.0;

    // Create shifting pattern using sine functions
    vec2 st = uv * freq;
    st.x += sin(st.y * 0.5 + iTime) * 0.5;
    st.y += cos(st.x * 0.7 - iTime * 2.0) * 0.3;

    // Calculate color components with time-based phase shift
    vec3 color = vec3(
        sin(st.x * 4.0 + iTime) * 0.5 + 0.5,
        cos(st.y * 4.0 - iTime * 2.0) * 0.5 + 0.5,
        sin((st.x + st.y) * 3.0 + iTime * 1.5) * 0.5 + 0.5
    );

    // Apply HSL color shift based on time and position
    vec3 hslColor = vec3(
        fract(sin(iTime * 0.2 + uv.x * 4.0) * 0.8 + 0.1),
        0.7,
        0.5 + sin(iTime * 0.3 + uv.y * 6.0) * 0.2
    );
    color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
