vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create pulsating circles with different parameters
    for(float i = 0.0; i < 5.0; i++) {
        float phase = i * 0.2;
        float speed = 1.0 + sin(iTime * 0.5 + phase) * 0.5;

        // Moving circle position
        vec2 pos = vec2(
            sin(iTime * speed + phase) * 0.5 + 0.5,
            cos(iTime * speed + phase) * 0.5 + 0.5
        );

        // Calculate distance from current pixel to circle center
        float dist = length(uv - pos);

        // Pulsating radius with time
        float radius = 0.2 + sin(iTime + phase) * 0.1;

        // Create color based on distance and time
        vec3 c = hsl2rgb(vec3(
            (iTime + dist * 5.0 + phase) * 0.2,
            0.7,
            0.6 + sin(iTime + dist * 5.0 + phase) * 0.2
        ));

        // Add turbulence effect
        float turb = turbulent(uv * 10.0 + iTime);
        c *= 1.0 + turb * 0.3;

        // Blend circles together with smooth falloff
        float blend = smoothstep(radius, radius + 0.1, dist);
        color += c * (1.0 - blend);
    }

    return vec4(color, 1.0);
}
