vec4 Effect2(vec2 uv) {
    // Scale UV coordinates into 0-1 range
    vec2 st = uv * 8.0;

    // Create three layers of sine waves with different frequencies and phase shifts
    float r = sin(st.x + iTime) * 0.5 + 0.5;
    float g = sin(st.y + iTime * 0.7 + 2.0) * 0.5 + 0.5;
    float b = sin(length(st) + iTime * 0.4 + 1.0) * 0.5 + 0.5;

    // Add turbulence for more dynamic movement
    vec3 color = vec3(r, g, b) + turbulent(vec2(uv.x * 4.0, uv.y * 4.0) + iTime);

    // Convert to HSL and back to RGB for smoother transitions
    vec3 hslColor = hsl(color.r * 360.0, 1.0, 0.5);
    vec3 finalColor = hsl2rgb(hslColor);

    return vec4(finalColor, 1.0);
}
