vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);

    // Add time-based rotation
    theta += iTime * 0.5;

    // Create color variations using HSL colorspace
    vec3 hslColor = vec3(
        0.5 + sin(theta + iTime) * 0.3,
        1.0,  // Saturation
        r * 0.5 + sin(r * 20.0 + iTime) * 0.2  // Lightness varies with radius and time
    );

    // Convert HSL to RGB and add some turbulence for dynamic effect
    vec3 color = hsl2rgb(hslColor);

    // Add distance-based falloff
    color *= pow(1.0 - r * 0.5, 3.0);

    return vec4(color, 1.0);
}
