vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for better pattern visibility
    vec2 st = uv * 10.0;

    // Create a time-varying base pattern using turbulence
    float t1 = turbulent(st + iTime);

    // Add another layer of turbulence with higher frequency
    float t2 = turbulent(st * 5.0 + iTime);

    // Combine turbulence values and add time variation
    float value = (t1 + t2) * 0.5;
    value += sin(iTime * 0.5) * 0.3;

    // Create color variations using HSL to RGB conversion
    vec3 color = hsl(
        fract(value * 4.0 + iTime),   // Hue variation
        0.7 + t1 * 0.3,              // Saturation
        0.5 + t2 * 0.5               // Lightness
    );

    // Add geometric transformations
    vec2 rotated = uv - 0.5;
    rotated *= mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime));
    rotated += 0.5;

    // Create a distance field from center
    float dist = length(rotated - 0.5);
    color *= smoothstep(0.3, 1.0, dist); // Add falloff

    return vec4(color, 1.0);
}
