vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create color wheel using HSL colors
    vec3 color = hsl(theta / (2.0 * PI), 0.5, 0.5);

    // Add pulsating rings effect
    float pulse = sin(iTime * 2.0) * 0.5 + 0.5;
    float ring = abs(r - 0.5 * (1.0 + pulse)) / 0.4;
    color.z *= 1.0 - ring; // Modulate lightness

    return vec4(hsl2rgb(color), 1.0);
}
