vec4 Effect2(vec2 uv) {
    // Create a sawtooth wave pattern with frequency 5.0
    float a = fract(iTime * 0.5 + uv.x * 5.0 + uv.y * 5.0);

    // Add noise to create variations in the shifting lines
    float noiseValue = noise(uv * 10.0) * 0.2;

    // Create a color palette using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.1 + noiseValue) * 360.0,  // Hue
        0.7 + noiseValue * 0.3,                     // Saturation
        0.5 + sin(iTime) * 0.2                    // Lightness
    ));

    // Combine the sawtooth pattern with the color palette
    return vec4(color * (1.0 - abs(a - 0.5)), 1.0);
}
