vec4 Effect2(vec2 uv) {
    // Create expanding wave pattern
    vec2 st = uv * 5.0; // Scale coordinates

    // Polar coordinates for expansion effect
    float r = length(st);
    float a = atan2(st.y, st.x);

    // Add noise to create dynamic movement
    float noiseVal = noise(vec3(r + iTime*0.5, a/PI, 0.0));
    float wave = sin(r - iTime) * (0.5 + noiseVal*0.5);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        a/(2.0*PI)*0.6 + 0.3, // Hue variation based on angle
        0.7 + wave*0.3,      // Saturation modulation
        0.5 + sin(iTime + r*10.0)*0.2 // Time-based brightness variation
    );

    // Convert to RGB and add turbulence effect
    vec3 color = hsl2rgb(hslColor);
    color *= 1.0 + turbulent(uv * 4.0) * 0.5;

    return vec4(color, 1.0);
}
