vec4 Effect2(vec2 uv) {
    // Create base position-dependent values
    vec2 pos = fract(uv * 100.0);

    // Add time-based variation
    float timeFactor = sin(iTime * 0.5 + pos.x * 10.0) * 0.5 + 0.5;

    // Combine position and time with sine functions
    vec3 colorBase = hsl2rgb(vec3(
        sin(pos.x * pi + iTime) * 0.5 + 0.5,
        sin(pos.y * pi + iTime * 0.7) * 0.5 + 0.5,
        0.8
    ));

    // Add turbulent noise variation
    float turb2D = turbulent(vec2(uv * 10.0 + iTime));
    float turb3D = turbulent(vec3(uv * 10.0, iTime));

    // Combine all elements for final color
    vec3 finalColor = mix(
        colorBase,
        hsl2rgb(vec3(
            sin(iTime * 0.3 + pos.y * pi) * 0.5 + 0.5,
            sin(pos.x * pi + iTime * 0.4) * 0.5 + 0.5,
            0.7
        )),
        (turb2D + turb3D) * 0.5
    );

    return vec4(finalColor, 1.0);
}
