vec4 Effect2(vec2 uv) {
    // Center the coordinates
    uv = uv * 2.0 - 1.0;

    // Convert to polar coordinates
    float r = length(uv);
    float a = atan2(uv.y, uv.x);

    // Create pulsating effect with time
    r += sin(iTime * 0.5 + a) * 0.5;

    // Create rings pattern with noise and turbulence
    float ring = sin(r * 10.0 + iTime) * 0.5 +
                 sin(r * 20.0 + iTime * 2.0) * 0.3 +
                 sin(r * 30.0 + iTime * 3.0) * 0.2;

    // Add movement to the rings
    ring += turbulent(vec2(r, a + iTime)) * 0.1;

    // Create vibrant color transitions
    vec3 color = hsl(a * 0.5 + iTime * 0.2, 1.0, 0.5);

    // Combine rings with colors
    color = mix(vec3(0.0), color, ring * 0.5 + 0.5);

    // Add glow effect
    if (r < 0.3) {
        color += vec3(1.0, 0.8, 0.6) * (1.0 - r / 0.3);
    }

    return vec4(color, 1.0);
}
