vec4 Effect2(vec2 uv) {
    vec2 st = uv * 5.0;
    float time = iTime * 0.5;

    // Create a base pattern using noise
    float n1 = noise(st + vec2(time, 0.0)) * 0.5 + 0.5;
    float n2 = noise(st + vec2(0.0, time)) * 0.5 + 0.5;

    // Create a rotating pattern
    vec2 rotatedUV = uv - 0.5;
    rotatedUV *= mat2(cos(time), sin(time), -sin(time), cos(time));
    rotatedUV += 0.5;

    // Add turbulence for more complexity
    float turb = turbulent(rotatedUV * 4.0 + time) * 0.5 + 0.5;

    // Combine patterns with color shifts
    vec3 color1 = hsl2rgb(vec3((n1 * 360.0 + time * 180.0) / 360.0, 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3((n2 * 360.0 - time * 180.0) / 360.0, 1.0, 0.5));

    // Mix colors and add depth
    vec3 finalColor = mix(color1, color2, turb);
    finalColor *= sin(time + uv.x * 2.0 + uv.y * 3.0) * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
