vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a rotating pattern using polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x) + time;
    float radius = length(st);

    // Add noise and turbulence for dynamic patterns
    float noiseVal = turbulent(vec3(uv * 5.0, time));
    vec3 colorBase = hsl2rgb(vec3(angle * 0.1, 0.7, 0.5 + noiseVal * 0.5));

    // Create a layered effect with additional noise
    float noiseLayer = noise(vec3(uv * 10.0 + time, 0.0)) * 0.5;
    vec3 colorLayer = hsl2rgb(vec3(angle * 0.2 + 1.0, 0.6, 0.4 + noiseLayer));

    // Combine the layers and apply transformations
    vec3 finalColor = mix(colorBase, colorLayer, noiseLayer);
    finalColor *= 1.0 - radius * 0.5;
    finalColor += vec3(noise(vec2(uv.x * 20.0 + time, uv.y * 20.0)) * 0.2);

    return vec4(finalColor, 1.0);
}
