vec4 Effect2(vec2 uv) {
    // Create base color with time-based shifting
    vec3 baseColor = hsl2rgb(vec3(0.5 * sin(iTime * 0.5) + 0.5, 1.0, 0.5));

    // Add noise and turbulence for dynamic patterns
    float noise1 = turbulent(uv * 10.0 + iTime);
    float noise2 = turbulent((uv * 20.0 + iTime * 0.5) + noise1);

    // Combine noise layers with different frequencies
    float combinedNoise = mix(noise1, noise2, 0.3 * sin(iTime));

    // Create color variations using HSL to RGB conversion
    vec3 finalColor = hsl2rgb(vec3(
        0.5 + 0.5 * sin(iTime) + combinedNoise * 0.3,
        1.0 - abs(combinedNoise * 0.5),
        0.5 + 0.5 * cos(iTime)
    ));

    // Add some warping to the UV coordinates
    vec2 warpedUV = uv + vec2(0.1, 0.1) * noise(uv * 0.5 + iTime);

    // Final color with time-based intensity modulation
    return vec4(finalColor * (0.5 + 0.5 * sin(iTime)), 1.0);
}
