vec4 Effect2(vec2 uv) {
    // Create dynamic color variations using sine functions and time
    vec2 st = uv * 10.0 + sin(uv * 5.0 + iTime) * 3.0;

    // Generate smooth color transitions using HSL colorspace
    vec3 baseColor = hsl(
        sin(iTime * 0.5 + st.x * 2.0) * 0.5 + 0.5,
        0.7 + sin(st.y * 3.0 + iTime * 2.0) * 0.3,
        0.5 + turbulent(vec2(st)) * 0.4
    );

    // Add turbulence for more dynamic movement
    float turb = turbulent(vec2(uv * 16.0 + iTime));
    baseColor += vec3(turb * sin(iTime) * 0.3);

    return vec4(hsl2rgb(baseColor), 1.0);
}
