vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    vec3 noise = texture(iChannel0, uv * 10.0).rgb;
    // Dynamic sine-based color manipulation
    vec3 color = sin(vec3(
        time + uv.x * 2.0,
        time + uv.y * 2.0,
        time + uv.x + uv.y
    )) * 0.5 + 0.5;

    // Add noise and turbulence to create variation
    color += vec3(
        noise.r * sin(time * 1.5),
        noise.g * cos(time * 1.2),
        noise.b * sin(time * 1.8)
    ) * 0.3;

    // Create optical illusion effect with HSL conversion
    color = hsl2rgb(vec3(
        (uv.x + uv.y) * 0.5 + time * 0.2,
        0.7 + sin(time * 0.8) * 0.2,
        0.5 + cos(time * 0.6) * 0.2
    ));

    // Add turbulence effect to UV coordinates
    vec2 turbulentUV = uv + vec2(
        turbulent(uv * time) * 0.1,
        turbulent(uv * time + 1.0) * 0.1
    );

    // Final color mix with noise
    color = mix(color, vec3(1.0), 
                noise.r * sin(time * 2.0) * 0.5 + 0.5);

    return vec4(color, 1.0);
}
