vec4 Effect2(vec2 uv) {
    vec2 st = uv - 0.5;
    float radius = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Create rotating color pattern using sine waves
    vec3 color = hsl2rgb(vec3(
        (theta * 0.16) + sin(iTime * 0.5) * 0.5,
        0.7 + turbulent(st * 50.0) * 0.3,
        0.5 + cos(radius * 4.0 + iTime) * 0.5
    ));

    // Add noise variation
    color += vec3(turbulent(vec3(st * 20.0, iTime)) * 0.1);

    return vec4(color, 1.0);
}
