vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 centered = uv - 0.5;
    float radius = length(centered);
    float angle = atan2(centered.y, centered.x) + iTime * 0.5; // Add time-based rotation

    // Create RGB color cycling effect using sine functions
    vec3 color = vec3(
        sin(angle + iTime) * 0.5 + 0.5,
        sin(angle + iTime + 2.0 * PI / 3.0) * 0.5 + 0.5,
        sin(angle + iTime + 4.0 * PI / 3.0) * 0.5 + 0.5
    );

    // Add turbulence effect to create a more dynamic pattern
    float turbulence = turbulent(vec2(radius, angle));
    color *= mix(1.0, abs(turbulence), 0.3);

    // Create multiple rotating wheels with different speeds and sizes
    float wheel1 = sin(angle * 2.0 + iTime) * radius;
    float wheel2 = sin(angle * 4.0 + iTime * 2.0) * (radius * 0.7);
    float wheel3 = sin(angle * 6.0 + iTime * 3.0) * (radius * 0.5);

    // Combine the wheels and color
    vec3 finalColor = mix(color, vec3(wheel1, wheel2, wheel3), smoothstep(0.0, 1.0, radius));

    return vec4(finalColor, 1.0);
}
