vec4 Effect2(vec2 uv) {
    // Create flowing pattern with turbulence and sine waves
    vec2 st = uv * 5.0;
    st += turbulent(st + iTime*0.5)*0.3;

    // Add oscillating sine wave patterns
    float wave1 = sin(st.x * 2.0 + iTime) * 0.5;
    float wave2 = sin(st.y * 2.0 + iTime*0.7) * 0.5;
    vec2 waves = vec2(wave1, wave2);

    // Combine patterns with color
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.2 + length(st), 1.0),
        0.8,
        0.6
    ));

    // Add lighting effect
    float light = sin(length(uv - 0.5) * 4.0 + iTime*0.3);
    color *= 1.0 + light * 0.5;

    return vec4(color, 1.0);
}
