vec4 Effect2(vec2 uv) {
    // Create pulsating factor over time
    float pulse = sin(iTime * 0.5 + 1.0) * 0.5 + 0.5;

    // Scale and animate coordinates
    vec2 st = uv * (1.0 + pulse * 2.0);

    // Create lattice pattern using sine waves
    float lines = sin(st.x * 8.0 - iTime) * 0.5 +
                  sin(st.y * 8.0 - iTime) * 0.5;

    // Add noise for complexity
    vec2 noiseSt = st + vec2(noise(vec3(st, iTime)) * 0.1);
    float noiseVal = noise(noiseSt * 4.0);

    // Combine lattice and noise
    float pattern = lines + noiseVal;

    // Create pulsating effect
    pattern *= (1.0 + sin(iTime) * 0.5);

    // Convert to RGB with HSL color
    vec3 color = hsl2rgb(vec3(180.0, 0.7, 0.5));

    // Create final output
    return vec4(color, max(pattern - 0.5, 0.0) * (1.0 + sin(iTime * 0.5)));
}
